"use strict";

var Utils = function() {};

Utils.DirectionEnum = {
    North:      0,
    NorthEast:  1,
    East:       2,
    SouthEast:  3,
    South:      4,
    SouthWest:  5,
    West:       6,
    NorthWest:  7
};

Utils.Coord = function(_x, _y)
{
    this.x = _x;
    this.y = _y;

    this.equals = function(_coord)
    {
        return this.x == _coord.x && this.y == _coord.y;
    };

    this.isValid = function()
    {
        return (this.x != -1 && this.y != -1);
    }

    this.toString = function()
    {
        return "(" + this.x + ", " + this.y + ")";
    }
};

Utils.Map = function(_width, _height)
{
    this.width = _width;
    this.height = _height;

    this.values = new Array(this.width * this.height); // set size


    this.get = function(_x, _y)
    {
        return this.values[_y * this.width + _x];
    };

    this.set = function(_x, _y, _value)
    {
        this.values[_y * this.width + _x] = _value;
    };

    this.toString = function()
    {
        var str = "";
        for (var i=0; i<this.height; ++i)
        {
            str += "[";
            for (var j=0; j<this.width; ++j)
            {
                if (j == 0)
                {
                    str += "" + this.get(j, i);
                }
                else
                {
                    str += ", " + this.get(j, i);
                }
            }
            str += "]\n";
        }

        return str;
    };
};

// EXPORT
module.exports = Utils;